mapper.select:
/*com.rootech.msolver.mapper.UserMapper.selectUser */
select a.user_id user_id
		, a.password password
		, a.enabled enabled
		, b.authority authority
from tb_ms_user a, tb_ms_user_authority b

	 a.user_id = b.user_id
	
		and a.user_id = #{userId}
	
	
		and a.password = #{password}

/*com.rootech.msolver.mapper.UserMapper.selectLoginUser */ select a.user_id userId , a.password password , a.enabled enabled , a.user_name userName , a.email email , b.authority authority , c.authority_name authorityName from tb_ms_user a, tb_ms_user_authority b, tb_ms_authority c
a.user_id = b.user_id and b.authority = c.authority
and a.user_id = #{userId}

mapper.insert:
/*com.rootech.msolver.mapper.UserMapper.insertUser */
insert into tb_ms_user (
	  user_id
	, password
	, user_name
	, email
	, enabled
	, rgst_date
	, rgst_id
	, upd_date
	, upd_id
) VALUES (
	  #{userId}  
	, #{password} 
	, #{userName}
	, #{email}
	, TRUE
	, Now()
	, #{userId}  
	, Now()
	, #{userId}   
)

mapper.update:
/* com.rootech.msolver.mapper.UserMapper.updateUser */
update tb_ms_user
   set
		, password 	= #{password} 
		, user_name	= #{userName} 
		, enabled  	= #{enabled}         
		, upd_date 	= Now()       
		, upd_id   	= #{userId}   
 where	user_id = #{userId}

mapper.delete:
/* com.rootech.msolver.mapper.UserMapper.deleteUser */
delete from tb_ms_user

	
		and user_id = #{userId}
	
	
		and password = #{password}

mapper.insert:
insert into tb_ms_user_authority 
	(
	 	user_id
		, authority
	)
	VALUES 
	(
		#{userId}
		, 'ROLE_USER'
	)
